import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { IoMdSearch } from 'react-icons/io';
import { FaSpinner } from 'react-icons/fa';

import Select from 'react-select';
import SubmitButton from '../../components/SubmitButton';
import api from '../../services/api';
import Container from '../../components/Container';

import { Loading, Owner, Form, IssueList } from './styles';

export default class Repository extends Component {
  static propTypes = {
    match: PropTypes.shape({
      params: PropTypes.shape({
        repository: PropTypes.string,
      }),
    }).isRequired,
  };

  state = {
    repository: {},
    issues: [],
    loading: true,
    state: 'all',
    page: 1,
    searching: false,
  };

  async componentDidMount() {
    const { match } = this.props;

    const repoName = decodeURIComponent(match.params.repository);

    const [repository, issues] = await Promise.all([
      api.get(`/repos/${repoName}`),
      api.get(`/repos/${repoName}/issues`, {
        params: {
          state: 'all',
          per_page: 5,
        },
      }),
    ]);

    this.setState({
      repository: repository.data,
      issues: issues.data,
      loading: false,
    });
  }

  handleFilterSelection = e => {
    this.setState({
      state: e.value,
    });
  };

  handleSubmit = async e => {
    e.preventDefault();

    this.setState({
      searching: true,
    });

    const { repository, state } = this.state;

    const response = await api.get(`/repos/${repository.full_name}/issues`, {
      params: {
        state,
        per_page: 30,
      },
    });

    this.setState({
      issues: response.data,
      searching: false,
    });
  };

  render() {
    const { repository, issues, loading, searching } = this.state;

    const options = [
      { value: 'all', label: 'All issues' },
      { value: 'open', label: 'Open issues' },
      { value: 'closed', label: 'Closed issues' },
    ];
    const defaultOption = options[0];

    if (loading) {
      return <Loading>Loading</Loading>;
    }

    return (
      <Container>
        <Owner>
          <Link to="/">Back to repository list</Link>
          <img src={repository.owner.avatar_url} alt={repository.owner.login} />
          <h1>{repository.name}</h1>
          <p>{repository.description}</p>
        </Owner>

        <Form onSubmit={this.handleSubmit}>
          <div>
            <Select
              options={options}
              defaultValue={defaultOption}
              onChange={this.handleFilterSelection}
            />
          </div>
          <SubmitButton loading={searching}>
            {searching ? (
              <FaSpinner color="#FFF" size={14} />
            ) : (
              <IoMdSearch color="#FFF" size={14} />
            )}
          </SubmitButton>
        </Form>

        <IssueList>
          {issues.map(issue => (
            <li key={String(issue.id)}>
              <img src={issue.user.avatar_url} alt={issue.user.login} />
              <div>
                <strong>
                  <a href={issue.html_url}>{issue.title}</a>
                  {issue.labels.map(label => (
                    <span key={String(label.id)}>{label.name}</span>
                  ))}
                </strong>
                <p>{issue.user.login}</p>
              </div>
            </li>
          ))}
        </IssueList>
      </Container>
    );
  }
}
